mapper.select:
select B.portfolio_id,B.portfolio_pic,B.member_id,B.member_name,B.portfolio_title,B.portfolio_best,B.project_category_id ,B.portfolio_intro,B.portfolio_update_date from ( select rownum as rnum ,A.portfolio_id,A.portfolio_pic,A.member_id,A.member_name,A.portfolio_title,A.portfolio_best,A.project_category_id ,A.portfolio_intro,A.portfolio_update_date from ( select * from portfolio_simple_view where
project_category_id=#{category}
OR
portfolio_title like '%'||#{criteria.portfolioTitle}||'%'
order by portfolio_update_date) A where rownum <= ${perPageNum*page}) B where B.rnum >= ${((page-1)*10)+1}
